package Variable;



public class IncerementDecerement1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=a++ + ++a;
System.out.println(b);
System.out.println(b++);
System.out.println(b);
System.out.println(b++);
System.out.println(b);
System.out.println(--a);

	}
}
